import http from './http';

const schedulerRequestConfig = { baseURL: '/' };
const basePath = '/scheduler/api/scheduler';

export const fetchNodeList = () => http.get(`${basePath}/nodes`, schedulerRequestConfig);

export const fetchNodeMetrics = () => http.get(`${basePath}/nodes/metrics`, schedulerRequestConfig);

export const fetchNodeHeartbeat = (nodeId) =>
  http.get(`${basePath}/nodes/${nodeId}/heartbeat`, schedulerRequestConfig);

export const offlineNode = (nodeId) =>
  http.post(`${basePath}/nodes/${nodeId}/offline`, undefined, schedulerRequestConfig);

export const rebalanceShards = (nodeId) =>
  http.post(`${basePath}/nodes/${nodeId}/rebalance`, undefined, schedulerRequestConfig);
