import http from './http';

export const login = (payload) => http.post('/auth/login', payload);

export const refreshToken = (refreshToken) =>
  http.post('/auth/refresh', { refreshToken });

export const fetchProfile = () => http.get('/auth/profile');

export default {
  login,
  refreshToken,
  fetchProfile
};
