import { fetchNodeList, fetchNodeMetrics, offlineNode, rebalanceShards } from '../../api/nodes';

const state = () => ({
  nodes: [],
  metrics: {
    totalNodes: 0,
    onlineNodes: 0,
    offlineNodes: 0,
    avgCpu: 0,
